INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347752, 'Amobarbital', 'Metoprolol', 'Moderate', 'Coadministration with barbiturates may decrease the plasma concentrations and pharmacologic effects of certain beta-blockers when administered orally. The proposed mechanism is barbiturate induction of hepatic microsomal and first-pass metabolism.', 'DDInter', 1767317475, 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days. Pharmacologic response to beta-blockers should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy, and the beta-blocker dosage adjusted as necessary. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'Metabolism', 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347753, 'Amobarbital', 'Metronidazole', 'Moderate', 'Coadministration with a barbiturate may significantly decrease the plasma concentrations and pharmacologic effects of nitroimidazoles. The proposed mechanism is barbiturate induction of nitroimidazole metabolism via hepatic microsomal enzymes.', 'DDInter', 1767317475, 'Patients treated concomitantly with a barbiturate may require a higher dosage of metronidazole to achieve a cure. Two- to threefold dosage increases are not uncommon. Pharmacologic response to metronidazole should be monitored and the dosage adjusted as necessary. Given its structural similarities to metronidazole, the same precaution may be applicable during therapy with tinidazole, although clinical data are lacking.', 'Metabolism', 'Patients treated concomitantly with a barbiturate may require a higher dosage of metronidazole to achieve a cure.', NULL, NULL, 'Moderate', 'pharmacodynamic');
