INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347760, 'Amobarbital', 'Mitotane', 'Minor', 'Mitotane could theoretically decrease the plasma levels and clinical effectiveness of barbiturates by inducing their hepatic metabolism. Until more data are available, be cognizant of the possibility of decreased clinical effectiveness from barbiturates when mitotane is added to a patient''s regimen, as well as the risk of barbiturate toxicity upon mitotane withdrawal.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
