INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347771, 'Amobarbital', 'Nelfinavir', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347772, 'Amobarbital', 'Nicardipine', 'Moderate', 'Barbiturates may increase the hepatic metabolism of some dihydropyridine calcium channel blockers by inducing CYP450 3A4 hepatic metabolism. The serum concentrations and effects of these calcium channel blockers may be significantly decreased.', 'DDInter', 1767317475, 'If concurrent administration cannot be avoided, the patient should be closely monitored for changes in pharmacologic effects whenever a barbiturate is initiated, discontinued or its dosage adjusted. An increased dosage of the calcium channel blocker may be necessary.', 'Metabolism', 'If concurrent administration cannot be avoided, the patient should be closely monitored for changes in pharmacologic effects whenever a barbiturate is initiated, discontinued or its dosage adjusted.', NULL, NULL, 'Moderate', 'pharmacodynamic');
