INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347781, 'Amobarbital', 'Olaparib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of olaparib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised during concomitant use of olaparib with CYP450 3A4 inducers. The potential for decreased efficacy of olaparib should be considered.', 'Metabolism', 'Caution is advised during concomitant use of olaparib with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347782, 'Amobarbital', 'Oliceridine', 'Major', 'Barbiturates may potentiate the central nervous system (CNS) depressant effects of opioids. Concomitant use may result in profound sedation, respiratory depression, coma, and death. On the other hand, some barbiturates can also induce the hepatic metabolism of opioids that are metabolized by CYP450 3A4 such as butorphanol, fentanyl, hydrocodone, methadone and oxycodone, resulting in enhanced clearance. Reduced analgesic efficacy or withdrawal symptoms may occur in patients maintained on their opioid regimen following the addition of a barbiturate. Conversely, discontinuation of the barbiturate may increase plasma concentrations of the opioid and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 1767317475, 'The use of opioids in conjunction with other CNS depressants such as barbiturates should generally be avoided unless alternative treatment options are inadequate. Particular caution is advisable when a barbiturate is added to or withdrawn from therapy in patients receiving opioids that are CYP450 3A4 substrates, as there may be an increased risk of withdrawal symptoms (e.g., restlessness, insomnia, sweating, lacrimation, or rhinorrhea) following initiation of the barbiturate and overdose following discontinuation. A dosage adjustment for one or both drugs may be required.', 'Synergism', 'The use of opioids in conjunction with other CNS depressants such as barbiturates should generally be avoided unless alternative treatment options are inadequate.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347783, 'Amobarbital', 'Olopatadine (nasal)', 'Moderate', 'Concurrent use of olopatadine nasal spray with alcohol or other central nervous system (CNS) depressants may result in additive impairment of mental alertness and performance. Somnolence has been reported in some patients using olopatadine nasal spray in clinical trials.', 'DDInter', 1767317475, 'Concomitant use of olopatadine nasal spray with alcohol or other CNS depressants should generally be avoided if possible. In the event that they are used together, patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'Concomitant use of olopatadine nasal spray with alcohol or other CNS depressants should generally be avoided if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347784, 'Amobarbital', 'Opicapone', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
