INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347791, 'Amobarbital', 'Oxtriphylline', 'Moderate', 'Barbiturates may decrease serum levels and therapeutic effects of the methylxanthines. The mechanism is barbiturate induction of CYP450 3A4 and 1A2 hepatic metabolism of methylxanthines.', 'DDInter', 1767317475, 'Close observation for clinical and laboratory evidence of decreased methylxanthine effect is indicated if these drugs must be used together. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms.', 'Metabolism', 'Close observation for clinical and laboratory evidence of decreased methylxanthine effect is indicated if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
