INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347793, 'Amobarbital', 'Oxymorphone', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', 'DDInter', 1767317475, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347794, 'Amobarbital', 'Ozanimod', 'Moderate', 'Limited clinical data suggest that nonselective monoamine oxidase inhibitors (MAOIs) may enhance or prolong the central nervous system effects of barbiturates. The mechanism of interaction is unknown, but may involve inhibition of barbiturate metabolism by MAOIs. Somnolence, dizziness, ataxia, and semi-consciousness have been reported.', 'DDInter', 1767317475, 'Until more information is available, patients should be monitored for increased barbiturate effects during coadministration with an MAOI or other agents that possess MAOI activity such as furazolidone, linezolid, methylene blue, and procarbazine.', 'Metabolism', 'Until more information is available, patients should be monitored for increased barbiturate effects during coadministration with an MAOI or other agents that possess MAOI activity such as furazolidone, linezolid, methylene blue, and procarbazine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
