INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347890, 'Amobarbital', 'Telaprevir', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, which are metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is recommended if boceprevir or telaprevir is used in combination with a CYP450 3A4 inducer. Close clinical monitoring of the virologic response to the HCV NS3/4A protease inhibitor is recommended.', 'Metabolism', 'Caution is recommended if boceprevir or telaprevir is used in combination with a CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
