INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (40365, 'Tadalafil', 'Lorcaserin', 'Moderate', 'Theoretical concerns exist that coadministration of lorcaserin and drugs used for the treatment of erectile dysfunction may increase the risk of priapism (painful erections greater than 6 hours in duration). Lorcaserin may cause priapism due to 5-HT2C receptor agonism. If not treated promptly, priapism can result in irreversible damage to the erectile tissue.', NULL, 'There is limited experience with the combination of lorcaserin and medications indicated for erectile dysfunction.', NULL, 'There is limited experience with the combination of lorcaserin and medications indicated for erectile dysfunction. Caution is advised if coadministration is necessary, particularly in patients with risk factors for priapism such as sickle cell anemia, multiple myeloma, leukemia, or anatomical deformation of the penis (e.g., angulation, cavernosal fibrosis, Peyronie''s disease). Men who have an erection lasting longer than 4 hours, whether painful or not, should immediately discontinue these drugs and seek emergency medical attention.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40421/', '[1] "Product Information. Belviq (lorcaserin)." Eisai Inc, Teaneck, NJ.[2] "Product Information. Belviq (lorcaserin)." Eisai Inc, Teaneck, NJ.', 1767363021);
