INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (40377, 'Tolterodine', 'Lorcaserin', 'Minor', 'Coadministration with drugs that are inhibitors of CYP450 2D6 may increase the plasma concentrations of tolterodine, which is primarily metabolized by this isoenzyme in patients that are extensive metabolizers, or EMs (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent). Moreover, since the active 5-hydroxymethyl metabolite (5-HM) generated by CYP450 2D6 is equipotent compared with tolterodine, and the sum of unbound serum concentrations of tolterodine and 5-HM are only 25% higher during the interaction, little alteration in the overall pharmacologic activity would be expected. Thus, dosage reductions should not be necessary.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40433/', '[1] Brynne N, Svanstrom C, AbergWistedt A, Hallen B, Bertilsson L "Fluoxetine inhibits the metabolism of tolterodin-pharmacokinetic implications and proposed clinical relevance." Br J Clin Pharmacol 48 (1999): 553-63[2] "Product Information. Detrol (tolterodine)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (40378, 'Tranylcypromine', 'Lorcaserin', 'Major', 'Concomitant use of lorcaserin with agents that possess or enhance serotonergic activity such as selective serotonin reuptake inhibitors (SSRIs), selective serotonin-norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), 5-HT1 receptor agonists (triptans), ergot alkaloids, phenylpiperidine opioids, bupropion, dextromethorphan, linezolid, lithium, St. John''s wort, tramadol, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', NULL, 'Caution is advised if lorcaserin must be used in combination with other agents that affect the serotonergic neurotransmitter system.', NULL, 'Caution is advised if lorcaserin must be used in combination with other agents that affect the serotonergic neurotransmitter system. Patients should be closely monitored for symptoms of the serotonin syndrome, which may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40434/', '[1] "Product Information. Belviq (lorcaserin)." Eisai Inc, Teaneck, NJ.[2] "Product 