INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80231, 'Dasatinib', 'Panobinostat', 'Major', 'Panobinostat can cause dose-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Concomitant use of panobinostat with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Concomitant use of panobinostat with other drugs that can prolong the QT interval should generally be avoided. Anti-emetic drugs with known QT prolonging risk such as dolasetron, granisetron, ondansetron, palonosetron, and tropisetron may be used with frequent ECG monitoring. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting panobinostat therapy and periodically during treatment as clinically indicated.', 'Synergism', 'Dacomitinib, Tepotinib, Selumetinib, Cobimetinib, Capmatinib, Afatinib, Tivozanib, Brigatinib, Axitinib, Ripretinib, Alpelisib, More', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Porfimer sodium, Selinexor, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80332/', '[1] "Product Information. Farydak (panobinostat)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies