INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80415, 'Dasatinib', 'Vorapaxar', 'Major', 'Coadministration of dasatinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with dasatinib is associated with severe thrombocytopenia.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with dasatinib.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with dasatinib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. A complete blood count (CBC) with differential and platelet count should be obtained prior to and at least weekly for the first 2 months, then monthly thereafter or as clinically indicated. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Entrectinib, Imatinib, Dabrafenib, Asciminib, Dacomitinib, Tepotinib, Bosutinib, Vemurafenib, Lapatinib, Ribociclib, Vandetanib, More', 'Protein C, Acenocoumarol, Selexipag', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80516/', '[1] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[3] Cer