INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80449, 'Delavirdine', 'Daunorubicin', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', NULL, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Valrubicin, Plicamycin, Bleomycin, Mitomycin, Dactinomycin, Epirubicin, Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Etravirine, Dolutegravir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80550/', '[1] "Product Information. Daunoxome (daunorubicin liposomal)." Nexstar Pharmaceuticals Inc, Boulder, CO.[2] "Product Information. Idamycin (idarubicin)." Pharmacia and Upjohn, Kalamazoo, MI.[3] "Multum Information Services, Inc. Expert Review Panel"[4] "Product Information. Cerubidine (daunorubicin)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80450, 'Denosumab', 'Daunorubicin', 'Moderate', 'Concomitant use of immunosuppressive or myelosuppressive agents with denosumab may increase the risk of serious infections. Denosumab binds to and inhibits the receptor activator of nuclear factor kappa-B ligand (RANKL), which is expressed on activated T and B lymphocytes and in lymph nodes. Thus, denosumab alone may increase the risk of infections.', NULL, 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents.', NULL, 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. The need for continued denosumab therapy should be assessed when serious infections occur during treatment.', 'Synergism', 'Valrubicin, Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Pegaspargase, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, More', 'Tiludronic acid, Alendronic acid, Vosoritide, Etidronic acid, Ibandronate, Cholecalciferol, Burosumab, Risedronic acid, Romosozumab, Zoledronic acid, Pamidronic acid', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80551/', '[1] "Product Information. Prolia (denosumab)." Amgen USA, Thousand Oaks, CA.[2] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.', 1767369485);
