INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80457, 'Dofetilide', 'Daunorubicin', 'Major', 'Like other class III antiarrhythmic agents, dofetilide can cause dose-related QT interval prolongation. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of dofetilide with other drugs that can prolong the QT interval has not been studied and is not recommended.', NULL, 'Coadministration of dofetilide with other drugs that can prolong the QT interval has not been studied and is not recommended.', 'Synergism', 'Lidocaine, Tocainide, Mexiletine, Bretylium, Moricizine', 'Valrubicin, Plicamycin, Ixabepilone, Mitoxantrone, Bleomycin, Mitomycin, Dactinomycin, Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80558/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[5] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[6] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[7] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[8] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80458, 'Dolasetron', 'Daunorubicin', 'Major', 'Dolasetron can cause dose-related prolongation of the QT interval via its pharmacologically active metabolite, hydrodolasetron. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if dolasetron is used in combination with cumulative high-dose anthracycline therapy or other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if dolasetron is used in combination with cumulative high-dose anthracycline therapy or other drugs that can prolong the QT interval. ECG monitoring may be appropriate in high-risk patients. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Valrubicin, Plicamycin, Ixabepilone, Bleomycin, Mitomycin, Dactinomycin, Pertuzumab, Olaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, More', 'Scopolamine, Nabilone, Aprepitant, Dronabinol', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80559/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Anzemet (dolasetron)." Hoechst Marion-Roussel Inc, Kansas City, MO.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80459, 'Donepezil', 'Daunorubicin', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', 'Valrubicin, Plicamycin, Ixabepilone, Bleomycin, Mitomycin, Dactinomycin, Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, More', 'Memantine, Ginkgo biloba, Aducanumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80560/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767369485);
