INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80701, 'Daunorubicin', 'Tuberculin purified protein derivative', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Valrubicin, Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Anagrelide, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80802/', '[1] "Product Information. Spherulin (coccidioidin skin test)." ALK Laboratories Inc, Wallingford, CT.[2] "Product Information. Tuberculin PPD (Mantoux) (tuberculin purified protein derivative)." Connaught Laboratories Ltd, Toronto, ON.[3] "Product Information. Candin (candida albicans extract)." Nielsen Biosciences Inc, San Diego, CA.[4] "Product Information. MSTA Mumps Skin Test Antigen (mumps skin test antigen)." Aventis Pharmaceuticals, Swiftwater, PA.[5] "Product Information. Multitest CMI (skin test antigens, multiple)." Aventis Pharmaceuticals, Swiftwater, PA.[6] "Product Information. Histolyn-Cyl (histoplasmin)." ALK Laboratories Inc, Wallingford, CT.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80702, 'Daunorubicin', 'Tucatinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', NULL, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Valrubicin, Plicamycin, Bleomycin, Mitomycin, Dactinomycin, Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, More', 'Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Cobimetinib, Ibrutinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80803/', '[1] "Product Information. Daunoxome (daunorubicin liposomal)." Nexstar Pharmaceuticals Inc, Boulder, CO.[2] "Product Information. Idamycin (idarubicin)." Pharmacia and Upjohn, Kalamazoo, MI.[3] "Multum Information Services, Inc. Expert Review Panel"[4] "Product Information. Cerubidine (daunorubicin)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80703, 'Daunorubicin', 'Typhoid vaccine (inactivated)', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'Valrubicin, Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Anagrelide, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80804/', '[1] "Product Information. Imovax Rabies (rabies vaccine)." sanofi pasteur, Swiftwater, PA.[2] Department of Health. National Health Service "Immunisation Against Infectious Disease - "The Green Book". Chapter 6: Contraindications and special considerations. Available from: URL: https://assets.publishing.service.gov.uk/government/uploads/system/uploads/attachment_data/file/655225/Greenbook_chap" ([2017 Aug]):[3] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[4] "Product Information. Havrix (hepatitis A vaccine)." SmithKline Beecham, Philadelphia, PA.[5] "Product Information. Fluzone Influenza Vaccine (influenza virus vaccine)." Connaught, Swiftwater, PA.[6] CDC Centers for Disease Control and Prevention "General Best Practice Guidelines for Immunization: Altered Immunocompetence. Available from: URL: https://www.cdc.gov/vaccines/hcp/acip-recs/general-recs/immunocompetence.pdf." ([2017, Apr 17]):[7] "Product Information. Biothrax (anthrax vaccine adsorbed)." Emergent BioSolutions Inc., Lansing, MI.[8] "Product Information. Omnihib (haemophilus b conjugate vaccine)." SmithKline Beecham, Philadelphia, PA.[9] Cerner Multum, Inc. "Australian Product Information." O 0[10] "Product Information. Influenza Virus Vaccine, H5N1, Inactivated (influenza virus vaccine, H5N1, inactivated)." GlaxoSmithKline, Research Triangle Park, NC.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80704, 'Daunorubicin', 'Typhoid vaccine (live)', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'Valrubicin, Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Anagrelide, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80805/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[2] "Product Information. YF-Vax (yellow fever vaccine)." sanofi pasteur, Swiftwater, PA.[3] "Product Information. Attenuvax (measles virus vaccine, live, attenuated)." Merck & Co, Inc, West Point, PA.[4] Kriner P, Lopez K, Leung J, Harpaz R, Bialek SR "Notes from the field: varicella-associated death of a vaccinated child with leukemia - California, 2012." MMWR Morb Mortal Wkly Rep 63 (2014): 161[5] "Product Information. M-M-R II (measles/mumps/rubella virus vaccine)." Merck & Company Inc, West Point, PA.[6] Charkoudian LD, Kaiser GM, Steinmetz RL, Srivastava SK "Acute retinal necrosis after herpes zoster vaccination." Arch Ophthalmol 129 (2011): 1495-7[7] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[8] "Product Information. Meruvax II (rubella virus vaccine, live)." Merck & Co, Inc, West Point, PA.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80705, 'Daunorubicin', 'Ulipristal', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', NULL, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Valrubicin, Plicamycin, Ixabepilone, Bleomycin, Mitomycin, Dactinomycin, Pertuzumab, Olaparib, Oxaliplatin, Bortezomib, Tisagenlecleucel, More', 'Norelgestromin, Norethisterone, Etonogestrel, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Estradiol, Levonorgestrel, Medroxyprogesterone acetate, Desogestrel, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80806/', '[1] "Product Information. Daunoxome (daunorubicin liposomal)." Nexstar Pharmaceuticals Inc, Boulder, CO.[2] "Product Information. Idamycin (idarubicin)." Pharmacia and Upjohn, Kalamazoo, MI.[3] "Multum Information Services, Inc. Expert Review Panel"[4] "Product Information. Cerubidine (daunorubicin)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80706, 'Daunorubicin', 'Upadacitinib', 'Major', 'Coadministration of upadacitinib with other immuno- or myelosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies.', NULL, 'Close monitoring for the development of infection is recommended if upadacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy.', NULL, 'Close monitoring for the development of infection is recommended if upadacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. Lymphocyte and neutrophil counts as well as hemoglobin should be evaluated at baseline and regularly during therapy, and upadacitinib dosage adjusted as necessary in accordance with the product labeling. Patients should be advised to contact their physician if they develop signs or symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, or pain or burning during urination. If a serious or opportunistic infection develops, upadacitinib should be interrupted until the infection is controlled.', 'Others', 'Valrubicin, Pertuzumab, Vismodegib, Enasidenib, Ivosidenib, Sonidegib, Porfimer sodium, Estramustine, Aminolevulinic acid, Tazemetostat, Vorinostat, More', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80807/', '[1] "Product Information. Rinvoq (upadacitinib)." AbbVie US LLC, North Chicago, IL.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80707, 'Daunorubicin', 'Ustekinumab', 'Moderate', 'The use of ustekinumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy.', 'Others', 'Valrubicin, Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Pegaspargase, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, More', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80808/', '[1] "Product Information. Stelara (ustekinumab)." Centocor Inc, Malvern, PA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80708, 'Daunorubicin', 'Valbenazine', 'Moderate', 'Valbenazine may cause modest prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Central nervous system (CNS)-depressant effects may be additively or synergistically increased in patients taking valbenazine with certain other drugs that cause these effects, especially in elderly or debilitated patients.', NULL, 'Caution and clinical monitoring are recommended if concomitant use of valbenazine with other drugs that can prolong the QT interval is required.', NULL, 'Caution and clinical monitoring are recommended if concomitant use of valbenazine with other drugs that can prolong the QT interval is required. Valbenazine is not recommended for use in patients with congenital long QT syndrome or with arrhythmias associated with a prolonged QT interval. In patients with other risk factors for QT prolongation, the QT interval should be assessed before increasing the dose of valbenazine. The manufacturer recommends that valbenazine dosage be reduced to 40 mg once daily in patients on concomitant therapy with a strong CYP450 3A4 inhibitor (e.g., itraconazole, ketoconazole, clarithromycin). Valbenazine dose reduction should also be considered in patients on concurrent therapy with a strong CYP450 2D6 inhibitor (e.g., paroxetine, fluoxetine, quinidine), or in patients who are poor metabolizers of CYP450 2D6.', 'Synergism', 'Valrubicin, Plicamycin, Ixabepilone, Mitoxantrone, Bleomycin, Mitomycin, Dactinomycin, Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, More', 'Dextromethorphan, Dalfampridine, Sodium oxybate, Riluzole, Inotersen, Amifampridine, Tafamidis', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80809/', '[1] "Product Information. Ingrezza (valbenazine)." Neurocrine Biosciences, Inc., San Diego, CA.[2] "Product Information. Ingrezza (valbenazine)." Neurocrine Biosciences, Inc., San Diego, CA.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (80709, 'Daunorubicin', 'Valganciclovir', 'Moderate', 'The use of ganciclovir with other potentially myelotoxic and/or nephrotoxic agents may increase the risk and severity of hematologic toxicity and/or nephrotoxicity due to both additive pharmacodynamic effects and additive effects on the kidney. Ganciclovir alone may cause severe leucopenia, neutropenia, anemia, thrombocytopenia, pancytopenia, bone marrow depression, aplastic anemia, increased serum creatinine levels, and acute renal failure, and the risk may theoretically increase when coadministered with other myelotoxic or nephrotoxic therapy.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks. Extreme caution is advised if they are used in patients who have recently received or are receiving treatment with myelotoxic and/or nephrotoxic drugs, and vice versa. If coadministration is required, reduced dosages of one or more of the drugs may be required, and the patient should be monitored for the development of hematologic and/or renal adverse effects both during and after discontinuation of therapy.', 'Synergism', 'Valrubicin, Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Pegaspargase, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Etravirine, Dolutegravir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80810/', '[1] "Product Information. Valcyte (valganciclovir)" Roche Laboratories, Nutley, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Cytovene (ganciclovir)." Genentech, South San Francisco, CA.[5] "Product Information. Cytovene (ganciclovir)." Genentech, South San Francisco, CA.[6] Jung D, Griffy K, Dorr A "Effect of food on high-dose oral ganciclovir disposition in HIV-positive subjects." J Clin Pharmacol 39 (1999): 161-5[7] Lavelle J, Follansbee S, Trapnell CB, Buhles WC, Griffy KG, Jung D, Dorr A, Conner J "Effect of food on the relative bioavailability of oral ganciclovir." J Clin Pharmacol 36 (1996): 238-41', 1767369485);
