INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (40733, 'Alprazolam', 'Benzhydrocodone', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40789/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (40734, 'Alvimopan', 'Benzhydrocodone', 'Major', 'Recent exposure to opioids can increase the sensitivity to mu opioid receptor antagonists.', NULL, 'The use of alvimopan is contraindicated in patients who have received therapeutic doses of opioids for more than 7 consecutive days immediately prior to alvimopan administration.', NULL, 'The use of alvimopan is contraindicated in patients who have received therapeutic doses of opioids for more than 7 consecutive days immediately prior to alvimopan administration. Caution is advised in patients who have received more than 3 doses of an opioid within the week prior to surgery, as these patients were not studied in the postoperative ileus clinical trials. Patients with recent exposure to opioids should be monitored for increased adverse effects such as abdominal pain, nausea and vomiting, and diarrhea.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40790/', '[1] "Product Information. Entereg (alvimopan)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Zohydro ER (HYDROcodone)." Zogenix, Inc, San Diego, CA.[3] "Product Information. Entereg (alvimopan)." GlaxoSmithKline, Research Triangle Park, NC.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (40735, 'Amifampridine', 'Benzhydrocodone', 'Moderate', 'The use of amifampridine is associated with a dose-related risk of seizures, and the risk may be further increased when coadministered with other drugs that can reduce the seizure threshold. Seizures have occurred in patients without a history of seizures taking amifampridine at recommended dosages and at various times after initiation of treatment.', NULL, 'Caution is advised if amifampridine is coadministered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with other risk factors for seizures (e.', NULL, 'Caution is advised if amifampridine is coadministered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with other risk factors for seizures (e.g., head trauma; brain tumor; severe hepatic cirrhosis; metabolic disorders; CNS infections; excessive use of alcohol or sedatives; addiction to opiates, cocaine, or stimulants; diabetes treated with oral hypoglycemic agents or insulin). Consider discontinuation or dose reduction of amifampridine in patients who experience a seizure during treatment. Use of amifampridine is contraindicated in patients with a history of seizures.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40791/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] "Product Information. Firdapse (amifampridine)." Catalyst Pharmaceuticals, Inc., Coral Gables, FL.', 1767363021);
