INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1348141, 'Amphetamine', 'Tranylcypromine', 'Major', 'Centrally-acting sympathomimetic agents (i.e., CNS stimulants), particularly the amphetamines and amphetamine derivatives, may precipitate severe hypertensive reactions and hyperpyrexia in patients treated with monoamine oxidase inhibitors (MAOIs). MAOIs also slow amphetamine metabolism, which may potentiate amphetamine effect on the release of norepinephrine and other monoamines from adrenergic nerve endings.', 'DDInter', 1767317475, 'In general, CNS stimulants should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine).', 'Synergism', 'In general, CNS stimulants should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
