INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1348151, 'Amphetamine', 'Delavirdine', 'Moderate', 'According to the product labeling for delavirdine, coadministration of the drug may increase the plasma concentrations of amphetamines. The proposed mechanism is delavirdine inhibition of the CYP450 2D6 metabolism of amphetamines.', 'DDInter', 1767317475, 'Caution is advised if amphetamines are prescribed in combination with delavirdine. Patients should be monitored for excessive anorectic and central nervous system stimulant effects of amphetamines.', 'Metabolism', 'Caution is advised if amphetamines are prescribed in combination with delavirdine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1348152, 'Amphetamine', 'Deserpidine', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, may decrease the hypotensive effect of postganglionic adrenergic blocking agents such as guanadrel, guanethidine and rauwolfia alkaloids, which work by depleting catecholamine stores from adrenergic nerve endings. CNS stimulants can have peripheral sympathetic activity, thus they may elevate blood pressure on their own as well as antagonize some of the antiadrenergic effect produced by these hypotensive agents.', 'DDInter', 1767317475, 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants. If the combination is used, blood pressure and heart rate should be monitored.', 'Antagonism', 'Alternatives to postganglionic adrenergic blocking agents should be considered in hypertensive patients treated with CNS stimulants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
