INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1348165, 'Amphetamine', 'Sertraline', 'Major', 'Serotonin reuptake inhibitors may potentiate the pharmacologic response to sympathomimetic agents.', 'DDInter', 1767317475, 'In general, amphetamines and other sympathomimetic appetite suppressants should not be combined with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs). Close monitoring for enhanced sympathomimetic effects and possible serotonin syndrome is recommended if these agents must be used together.', 'Synergism', 'In general, amphetamines and other sympathomimetic appetite suppressants should not be combined with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs).', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1348166, 'Amphetamine', 'Sodium citrate', 'Moderate', 'Alkalinization of the urine can decrease the renal elimination of amphetamines. Amphetamine effects may be prolonged, and the risk of toxicity may be increased.', 'DDInter', 1767317475, 'Urinary alkalinizers should generally not be used with amphetamines, particularly in an amphetamine overdose situation.', 'Excretion', 'Urinary alkalinizers should generally not be used with amphetamines, particularly in an amphetamine overdose situation.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1348167, 'Amphetamine', 'Trifluoperazine', 'Moderate', 'Phenothiazines may antagonize the pharmacologic effects of amphetamine, amphetamine derivatives, and other centrally-acting sympathomimetic agents (i.e., CNS stimulants). Conversely, these agents may diminish the neuroleptic efficacy of phenothiazines. The exact mechanism of interaction is unknown but may involve opposing effects on dopaminergic activity.', 'DDInter', 1767317475, 'Amphetamine, amphetamine derivatives, and other CNS stimulants should generally not be used, particularly for weight reduction, in patients treated with phenothiazines.', 'Antagonism', 'Amphetamine, amphetamine derivatives, and other CNS stimulants should generally not be used, particularly for weight reduction, in patients treated with phenothiazines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1348168, 'Amphetamine', 'Trimipramine', 'Moderate', 'The use of tricyclic antidepressants in combination with amphetamines or sympathomimetic appetite suppressants may produce additive cardiovascular effects, increasing the risk of hypertension, cardiac arrhythmias, tachycardia, and fever. The mechanism involves additive pharmacodynamic effects resulting from increased norepinephrine release by sympathomimetic agents and inhibition of norepinephrine reuptake by tricyclic antidepressants. A pharmacokinetic interaction is also possible between tricyclic antidepressants and amphetamines, since many of these agents are metabolized by CYP450 2D6. Increased plasma levels of one or both drugs may occur during coadministration.', 'DDInter', 1767317475, 'Close monitoring of cardiovascular status is recommended for patients receiving this combination. Patients should be advised to promptly report symptoms such as fever, headache, or fast or irregular heartbeats.', 'Metabolism, Synergism', 'Close monitoring of cardiovascular status is recommended for patients receiving this combination.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1348169, 'Amphotericin B (lipid complex)', 'Amikacin (liposome)', 'Moderate', 'The potential nephrotoxicity associated with amphotericin B and aminoglycosides may be additive.', 'DDInter', 1767317475, 'Just before the start and just before the end of each amphotericin infusion, hydration of the patient with 500 mL of normal saline may be helpful. The patient''s renal function should be monitored frequently during therapy.', 'Synergism', 'Just before the start and just before the end of each amphotericin infusion, hydration of the patient with 500 mL of normal saline may be helpful.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1348170, 'Amphotericin B (lipid complex)', 'Arsenic trioxide', 'Major', 'Arsenic trioxide can cause QT interval prolongation and complete atrioventricular block. Since hypokalemia and hypomagnesemia are known risk factors for arrhythmia associated with QT prolongation, coadministration of arsenic trioxide with agents that can cause significant potassium and/or magnesium loss (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may increase the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Caution is advised if arsenic trioxide must be used concomitantly with medications that can cause significant potassium and/or magnesium loss. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with arsenic trioxide. Patients should also have frequent ECGs and be monitored for serious arrhythmias when QT intervals are prolonged. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'Caution is advised if arsenic trioxide must be used concomitantly with medications that can cause significant potassium and/or magnesium loss.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1348171, 'Amphotericin B (lipid complex)', 'Atracurium', 'Moderate', 'Amphotericin commonly causes renal potassium and magnesium wasting. As a result, serum potassium is decreased, and the pharmacologic effects of some neuromuscular blocking agents are enhanced.', 'DDInter', 1767317475, 'To decrease or prevent this interaction, magnesium and potassium levels should be monitored, and the patient''s body stores of potassium and magnesium should be replenished even before the expected fall in serum potassium occurs.', 'Excretion', 'To decrease or prevent this interaction, magnesium and potassium levels should be monitored, and the patient''s body stores of potassium and magnesium should be replenished even before the expected fall in serum potassium occurs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1348172, 'Amphotericin B (lipid complex)', 'Balsalazide', 'Moderate', 'Coadministration of 5-aminosalicylate with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of 5-aminosalicylate or its prodrugs has been associated with rare reports of renal impairment including minimal change nephropathy, acute and chronic interstitial nephritis, and renal failure.', 'DDInter', 1767317475, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during 5-aminosalicylate therapy.', 'Synergism', 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1348173, 'Amphotericin B (lipid complex)', 'Beclomethasone dipropionate', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', 'DDInter', 1767317475, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', NULL, NULL, 'Moderate', 'pharmacodynamic');
