    ) VALUES (1348299, 'Ampicillin', 'Atenolol', 'Moderate', 'INTERVAL: The oral coadministration of ampicillin and atenolol may result in decreased plasma concentrations of the latter, possibly due to impaired gastrointestinal absorption in the presence of ampicillin.', NULL, 'These results suggest that the interaction may be attenuated by dividing the ampicillin dosage into smaller, more frequent doses and/or staggering the times of administration of the two drugs.', NULL, 'These results suggest that the interaction may be attenuated by dividing the ampicillin dosage into smaller, more frequent doses and/or staggering the times of administration of the two drugs.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
