        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1348301, 'Ampicillin', 'Balsalazide', 'Moderate', 'Balsalazide may be rendered less effective when administered with certain antibiotics. Balsalazide is acted upon in the lower gut by bacterial azoreduction to produce mesalamine, the therapeutically active ingredient. Although no studies have been done, it is theorized that oral antibiotics can interfere with this reduction to mesalamine.', NULL, 'If the patient must receive oral antibiotics, consideration should be given to using another 5-aminosalicylate such as mesalamine.', NULL, 'If the patient must receive oral antibiotics, consideration should be given to using another 5-aminosalicylate such as mesalamine.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
