    ) VALUES (1348304, 'Ampicillin', 'Chloroquine', 'Moderate', 'INTERVAL: Concomitant administration of chloroquine may delay as well as decrease the gastrointestinal absorption of ampicillin. The exact mechanism of interaction is unknown, but may involve slowing of gastric emptying and enhancement of gut motility induced by chloroquine.', NULL, 'To avoid potentially subtherapeutic plasma concentrations of ampicillin during coadministration with chloroquine, the drugs should be administered at least 2 hours apart.', NULL, 'To avoid potentially subtherapeutic plasma concentrations of ampicillin during coadministration with chloroquine, the drugs should be administered at least 2 hours apart.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
