    ) VALUES (1348345, 'Anagrelide', 'Argatroban', 'Major', 'Concomitant use of thrombin inhibitors and antiplatelet agents may increase the risk of bleeding complications due to additive or synergistic effects on the clotting cascade.', NULL, 'Coadministration of thrombin inhibitors and antiplatelet agents should be undertaken with caution and only after thorough assessment of risks and benefits.', NULL, 'Coadministration of thrombin inhibitors and antiplatelet agents should be undertaken with caution and only after thorough assessment of risks and benefits. Close clinical and laboratory observation for bleeding complications is recommended.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
