INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82180, 'Deflazacort', 'Pitolisant', 'Moderate', 'Concomitant use of pitolisant with NSAIDs and/or corticosteroids may increase the risk of gastrointestinal adverse effects such as dyspepsia, abdominal pain or discomfort, and gastritis due to potential additive irritant effects on the gastrointestinal mucosa. However, the effects were described as mostly mild to moderate in severity. Data are not available on the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation.', NULL, 'Caution is advised if pitolisant is used in combination with NSAIDs and/or corticosteroids, particularly in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly or debilitated patients.', NULL, 'Caution is advised if pitolisant is used in combination with NSAIDs and/or corticosteroids, particularly in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly or debilitated patients. If concomitant therapy is required, patients should be advised to report signs and symptoms of adverse GI effects, including abdominal pain or discomfort, dyspepsia, gastroesophageal reflux disease, gastritis, or the appearance of black, tarry stools.', 'Synergism', 'Dextromethorphan, Valbenazine, Dalfampridine, Sodium oxybate, Riluzole, Tetrabenazine, Inotersen, Deutetrabenazine, Tafamidis', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82281/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82181, 'Deflazacort', 'Poliovirus type 1 antigen (formaldehyde inactivated)', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82282/', '[1] "Product Information. Imovax Rabies (rabies vaccine)." sanofi pasteur, Swiftwater, PA.[2] Department of Health. National Health Service "Immunisation Against Infectious Disease - "The Green Book". Chapter 6: Contraindications and special considerations. Available from: URL: https://assets.publishing.service.gov.uk/government/uploads/system/uploads/attachment_data/file/655225/Greenbook_chap" ([2017 Aug]):[3] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[4] "Product Information. Havrix (hepatitis A vaccine)." SmithKline Beecham, Philadelphia, PA.[5] "Product Information. Fluzone Influenza Vaccine (influenza virus vaccine)." Connaught, Swiftwater, PA.[6] CDC Centers for Disease Control and Prevention "General Best Practice Guidelines for Immunization: Altered Immunocompetence. Available from: URL: https://www.cdc.gov/vaccines/hcp/acip-recs/general-recs/immunocompetence.pdf." ([2017, Apr 17]):[7] "Product Information. Biothrax (anthrax vaccine adsorbed)." Emergent BioSolutions Inc., Lansing, MI.[8] "Product Information. Omnihib (haemophilus b conjugate vaccine)." SmithKline Beecham, Philadelphia, PA.[9] Cerner Multum, Inc. "Australian Product Information." O 0[10] "Product Information. Influenza Virus Vaccine, H5N1, Inactivated (influenza virus vaccine, H5N1, inactivated)." GlaxoSmithKline, Research Triangle Park, NC.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82182, 'Deflazacort', 'Polyethylene glycol (3350)', 'Moderate', 'The overuse or abuse of laxatives can cause significant loss of electrolytes and potentiate the risk of hypokalemia associated with corticosteroid therapy. Corticosteroids promote the retention of sodium and water and the excretion of potassium. Although these effects are primarily associated with mineralocorticoids like fludrocortisone, they may also occur with higher dosages of glucocorticoids or adrenocorticotropic agents, particularly if given systemically for longer than brief periods.', NULL, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages.', NULL, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages. During concomitant therapy with corticosteroids, particularly if fludrocortisone or large doses of a glucocorticoid or adrenocorticotropic agent is given, patients should be counseled to recognize potential signs and symptoms of hypokalemia such as fatigue, myalgia, and muscle weakness. If maintenance of bowel regularity is required, patients should be advised to exercise and increase fiber in the diet and/or consider the use of bulk-forming laxatives.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82283/', '[1] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8[2] Blumenthal M, Goldberg A, Brinckmann J, eds. "Herbal Medicine: Expanded Commission E Monographs." Newton, MA: Integrative Medicine Communications (2000):[3] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[4] Seale JP, Compton MR "Side-effects of corticosteroid agents." Med J Aust 144 (1986): 139-42[5] Swartz SL, Dluhy RG "Corticosteroids: clinical pharmacology and therapeutic use." Drugs 16 (1978): 238-55[6] Lieberman P, Patterson R, Kunske R "Complications of long-term steroid therapy for asthma." J Allergy Clin Immunol 49 (1972): 329-36[7] Ramsahoye BH, Davies SV, el-Gaylani N, Sandeman D, Scanlon MF "The mineralocorticoid effects of high dose hydrocortisone." BMJ 310 (1995): 656-7', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82183, 'Deflazacort', 'Polythiazide', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', NULL, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', NULL, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Clindamycin, Ciclopirox, Vemurafenib, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82284/', '[1] Morris GC, Egan JG, Jones MK "Hypokalaemic paralysis induced by bolus prednisolone in Graves'' disease." Aust N Z J Med 22 (1992): 312[2] Thomas TP "The complications of systemic corticosteroid therapy in the elderly." Gerontology 30 (1984): 60-5[3] "Product Information. Hydeltrasol (prednisolone)." Merck & Co, Inc, West Point, PA.[4] Ramsahoye BH, Davies SV, el-Gaylani N, Sandeman D, Scanlon MF "The mineralocorticoid effects of high dose hydrocortisone." BMJ 310 (1995): 656-7[5] Powell JR "Steroid and hypokalemic myopathy after corticosteroids for ulcerative colitis. Systemic and tropical application." Am J Gastroenterol 52 (1969): 425-32[6] Thorn GW "Clinical considerations in the use of corticosteroids." N Engl J Med 274 (1966): 775-81[7] Seale JP, Compton MR "Side-effects of corticosteroid agents." Med J Aust 144 (1986): 139-42[8] Chrousos GA, Kattah JC, Beck RW, Cleary PA "Side effects of glucocorticoid treatment. Experience of the Optic Neuritis Treatment Trial." JAMA 269 (1993): 2110-2', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82184, 'Deflazacort', 'Posaconazole', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of 21-desdeflazacort, the active metabolite of deflazacort that is formed by esterases after oral administration and further metabolized by CYP450 3A4 to several inactive metabolites.', NULL, 'The manufacturer suggests using one-third of the recommended dosage of deflazacort when coadministered with potent or moderate CYP450 3A4 inhibitors.', NULL, 'The manufacturer suggests using one-third of the recommended dosage of deflazacort when coadministered with potent or moderate CYP450 3A4 inhibitors. Some authorities recommend avoiding concomitant use of deflazacort and CYP450 3A inhibitors unless the benefit outweighs the risk, in which case patients should be monitored for systemic corticosteroid side effects.', 'Metabolism', 'Micafungin, Caspofungin, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82285/', '[1] "Product Information. Emflaza (deflazacort)." Marathon Pharmaceuticals, Deerfield, IL.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
