INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82597, 'Delafloxacin', 'Sarecycline', 'Moderate', 'Coadministration with sarecycline may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, kidney, and/or liver due to inhibition of P-gp-mediated efflux by sarecycline.', NULL, 'Caution is advised when sarecycline is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when sarecycline is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever sarecycline is added to or withdrawn from therapy.', 'Others', 'Enoxacin, Gatifloxacin, Norfloxacin, Nalidixic acid, Gemifloxacin, Ciprofloxacin, Lomefloxacin, Grepafloxacin, Moxifloxacin, Cinoxacin, Trovafloxacin, More', 'Tetracycline, Minocycline, Oxytetracycline, Doxycycline, Omadacycline, Eravacycline, Demeclocycline, Tigecycline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82698/', '[1] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82598, 'Delafloxacin', 'Selenium', 'Moderate', 'INTERVAL: Oral preparations that contain magnesium, aluminum, or calcium may significantly decrease the gastrointestinal absorption of quinolone antibiotics. Absorption may also be reduced by sucralfate, which contains aluminum, as well as other polyvalent cations such as iron and zinc. The mechanism is chelation of quinolones by polyvalent cations, forming a complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', NULL, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82699/', '[1] Teng R, Dogolo LC, Willavize SA, Friedman HL, Vincent J "Effect of Maalox and omeprazole on the bioavailability of trovafloxacin." J Antimicrob Chemother 39 Suppl B (1997): 93-7[2] Noyes M, Polk RE "Norfloxacin and absorption of magnesium-aluminum." Ann Intern Med 109 (1988): 168-9[3] Nix DE, Wilton JH, Ronald B, Distlerath L, Williams VC, Norman A "Inhibition of norfloxacin absorption by antacids." Antimicrob Agents Chemother 34 (1990): 432-5[4] "Product Information. Zagam (sparfloxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[5] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[6] "Product Informa