INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82600, 'Delafloxacin', 'Sevelamer', 'Moderate', 'INTERVAL: Concurrent administration of sevelamer hydrochloride may decrease the oral bioavailability of ciprofloxacin and other quinolones, potentially reducing antibiotic efficacy and increasing the risk of bacterial resistance. The proposed mechanism is reduced quinolone absorption due to chelation with the protonated amine groups of sevelamer in the intestine.', NULL, 'Oral quinolone antibiotics should not be administered simultaneously with sevelamer hydrochloride.', NULL, 'Oral quinolone antibiotics should not be administered simultaneously with sevelamer hydrochloride. The optimal length of time needed to separate ingestion of these drugs is currently unknown. Based on the fact that it took up to 4 hours to achieve ciprofloxacin Cmax in all 15 volunteers of the study, the authors suggested a separation time of at least 4 hours between sevelamer and ciprofloxacin administration to reduce the likelihood of a significant interaction, particularly in patients with end-stage renal disease whose gastrointestinal motility may be slowed.', 'Absorption', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, Promethazine, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82701/', '[1] Kays MB, Overholser BR, Mueller BA, Moe SM, Sowinski KM "Effects of sevelamer hydrochloride and calcium acetate on the oral bioavailability of ciprofloxacin." Am J Kidney Dis 42 (2003): 1253-9[2] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[3] "Product Information. Renagel (sevelamer)." Genzyme Corporation, Cambridge, MA.', 1767369485);
