INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82614, 'Delafloxacin', 'Tolvaptan', 'Moderate', 'Coadministration with tolvaptan may increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp). The proposed mechanism is decreased clearance due to inhibition of P-glycoprotein-mediated drug efflux in the intestine, liver, and/or kidney by tolvaptan.', NULL, 'Caution is advised when tolvaptan is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate.', NULL, 'Caution is advised when tolvaptan is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate. Alternatives should be considered whenever possible. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate following the initiation or discontinuation of tolvaptan. The prescribing information for the coadministered drug should be consulted for specific dosing recommendations.', 'Distribution', 'Conivaptan', 'Enoxacin, Gatifloxacin, Norfloxacin, Nalidixic acid, Gemifloxacin, Lomefloxacin, Grepafloxacin, Moxifloxacin, Cinoxacin, Trovafloxacin, Levofloxacin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82715/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Jynarque (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[4] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[5] "Product Information. Samsca (tolvaptan)." Ots