INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82680, 'Erythromycin', 'Delavirdine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of erythromycin, which is primarily metabolized by the isoenzyme. The use of erythromycin has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes. In addition, erythromycin may increase the plasma concentrations of drugs that are substrates of CYP450 3A4.', NULL, 'Concomitant use of erythromycin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of erythromycin with potent CYP450 3A4 inhibitors should generally be avoided.', 'Metabolism', 'Lincomycin, Azithromycin, Dirithromycin, Minocycline, Vitamin A, Isotretinoin, Chloramphenicol, Fluorometholone, Dapsone, Azelaic acid, Adapalene, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Velpatasvir, Elbasvir, Famciclovir, Tipranavir, Voxilaprevir, Ribavirin, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82781/', '[1] Iannini PB "Cardiotoxicity of macrolides, ketolides and fluoroquinolones that prolong the QTc interval." Expert Opin Drug Saf 1 (2002): 121-8[2] Ray WA, Murray KT, Meredith S, Narasimhulu SS, Hall K, Stein CM "Oral erythromycin and the risk of sudden death from cardiac causes." N Engl J Med 351 (2004): 1089-96[3] Welling PG, Huang H, Hewitt PF, Lyons LL "Bioavailability of erythromycin stearate: influence of food and fluid volume." J Pharm Sci 67 (1978): 764-6[4] Welling PG "Influe