INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82691, 'Everolimus', 'Delavirdine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and P-glycoprotein may significantly increase the plasma concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus. The risk of side effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia may be increased.', NULL, 'Concomitant use of everolimus with potent inhibitors of CYP450 3A4 and P-glycoprotein should generally be avoided.', NULL, 'Concomitant use of everolimus with potent inhibitors of CYP450 3A4 and P-glycoprotein should generally be avoided. Some authorities recommend avoiding concomitant use of everolimus during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Lamivudine, Cabotegravir, Maraviroc, Zanamivir, Rilpivirine, Dolutegravir, Oseltamivir, Brincidofovir, Stavudine, Elbasvir, Famciclovir, More', 'Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib, Golimumab, Ustekinumab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82792/', '[1] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Homma S, Takahashi KI, Nihei S, Kato F, Sugihara S, Nunoda S "The successful managemen