INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82704, 'Fluvastatin', 'Delavirdine', 'Moderate', 'Coadministration with delavirdine may increase the plasma concentrations of fluvastatin. The proposed mechanism is delavirdine inhibition of CYP450 2C9 and 3A4, the isoenzymes responsible for the metabolic clearance of fluvastatin. Although the interaction has not been specifically studied, high levels of HMG-CoA reductase inhibitory activity in plasma is known to be associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', NULL, 'Caution is advised if fluvastatin is prescribed in combination with delavirdine.', NULL, 'Caution is advised if fluvastatin is prescribed in combination with delavirdine. The lowest possible dosage of fluvastatin should be used. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed. Pravastatin is not expected to interact with delavirdine and may be a suitable alternative in patients receiving delavirdine.', 'Metabolism', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Telaprevir, Velpatasvir, Rilpivirine, More', 'Cholestyramine, Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Probucol, Ezetimibe, Niacin, Colesevelam, Gemfibrozil, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82805/', '[1] "Product Information. Rescriptor (delavirdine)." Pharmacia and Upjohn, Kalamazoo, MI.[2] "Product Information. Lescol (fluvastatin)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82705, 'Glasdegib', 'Delavirdine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of glasdegib, which is primarily metabolized by the isoenzyme. Increased exposure to glasdegib may increase the risk of serious adverse effects such as QT interval prolongation, anemia, febrile neutropenia, thrombocytopenia, hemorrhage, edema, and mucositis.', NULL, 'Concomitant use of glasdegib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of glasdegib with potent CYP450 3A4 inhibitors should generally be avoided. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. Otherwise, close monitoring for increased adverse effects is advisable, including more frequent ECG monitoring.', 'Metabolism', 'Lamivudine, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Porfimer sodium, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82806/', '[1] "Product Information. Daurismo (glasdegib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Daurismo (glasdegib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82706, 'Glycerol phenylbutyrate', 'Delavirdine', 'Moderate', 'Coadministration of glycerol phenylbutyrate may lead to increased concentrations of CYP450 2D6 substrates. The proposed mechanism is inhibition of CYP450 2D6 by phenylbutyrate, the active moiety of glycerol phenylbutyrate, which has been shown to be an in vitro inhibitor of this isoenzyme.', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol).', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Telaprevir, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, Agalsidase beta, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82807/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ravicti (glycerol phenylbutyrate)." Hyperion Therapeutics Inc, South San Francisco, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82707, 'Grepafloxacin', 'Delavirdine', 'Moderate', 'Based on in vitro inhibition data, coadministration with grepafloxacin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by grepafloxacin.', NULL, 'Caution is advised if grepafloxacin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if grepafloxacin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever grepafloxacin is added to or withdrawn from therapy.', 'Metabolism', 'Enoxacin, Gatifloxacin, Norfloxacin, Levofloxacin, Nalidixic acid, Gemifloxacin, Ciprofloxacin, Lomefloxacin, Moxifloxacin, Cinoxacin, Trovafloxacin, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82808/', '[1] "Product Information. Raxar (grepafloxacin)." Glaxo Wellcome, Research Triangle Park, NC.', 1767369485);
