INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82709, 'Guanfacine', 'Delavirdine', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of guanfacine, which is primarily metabolized by the isoenzyme. The risk of adverse reactions such as hypotension, bradycardia, and sedation may increase.', NULL, 'Caution and dosage adjustment are advised when guanfacine is administered with potent and moderate CYP450 3A4 inhibitors.', NULL, 'Caution and dosage adjustment are advised when guanfacine is administered with potent and moderate CYP450 3A4 inhibitors. For extended-release guanfacine, the manufacturers recommend reducing the dosage to half the recommended level during concomitant use . Further dosage adjustments may be required based on patient tolerance and response.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, More', 'Rauwolfia serpentina root, Reserpine, Methyldopa, Clonidine, Deserpidine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82810/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Intuniv (guanfacine)." Shire US Inc, Florence, KY.[3] "Product Information. Tenex (guanfacine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Intuniv (guanfacine)." Shire US Inc, Florence, KY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82710, 'Halofantrine', 'Delavirdine', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of halofantrine, resulting in an increased risk of QT interval prolongation and ventricular arrhythmias. The mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of halofantrine. Halofantrine has been associated with QT interval prolongation, ventricular arrhythmias, and sudden death, even at recommended dosages.', NULL, 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors.', NULL, 'Caution and close monitoring is recommended if halofantrine is prescribed with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors. The manufacturer recommends performing an ECG before initiating halofantrine therapy and cardiac monitoring during and for 8 to 12 hours after completion of therapy.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, More', 'Artesunate, Mefloquine, Lumefantrine, Pyrimethamine, Tafenoquine, Atovaquone, Primaquine, Proguanil, Sulfadoxine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82811/', 