INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82718, 'Indinavir', 'Delavirdine', 'Moderate', 'Coadministration with delavirdine may increase the plasma concentrations of indinavir. The mechanism is delavirdine inhibition of indinavir metabolism via CYP450 3A4.', NULL, 'Indinavir dosage should be reduced to 600 mg three times a day during coadministration with delavirdine.', NULL, 'Indinavir dosage should be reduced to 600 mg three times a day during coadministration with delavirdine.', 'Metabolism', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Acyclovir, More', 'Glecaprevir, Cabotegravir, Zanamivir, Lenacapavir, Valaciclovir, Telaprevir, Velpatasvir, Rilpivirine, Oseltamivir, Remdesivir, Famciclovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82819/', '[1] Developed by the panel of Clinical Practices for Treatment of HIV Infection convened by the Department of Health and Human Services (DHHS) "Guidelines for the use of antiretroviral agents in HIV-1-infected adults and adolescents. Available from: URL: http://AIDSinfo.nih.gov." ([2003 Nov 10]):[2] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[3] "Product Information. Rescriptor (delavirdine)." Pharmacia and Upjohn, Kalamazoo, MI.[4] Yeh KC, Deutsch PJ, Haddix H, Hesney M, Hoagland V, Ju WD, Justice SJ, Osborne B, Sterrett AT, Stone JA, Woolf E, Waldman S "Single-dose pharmacokinetics of indinavir and the effect of food." Antimicrob Agents Chemother 42 (1998): 332-8[5] Shelton MJ, Wynn HE, Newitt RG, DiFrancesco R "Effects of grapefruit juice on pharmacokinetic exposure t