INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82733, 'Delavirdine', 'Larotrectinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of larotrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to larotrectinib may increase the risk of adverse effects such as neurotoxicity (delirium, dysarthria, dizziness, gait disturbance, paresthesia, encephalopathy, memory impairment, tremor) and hepatotoxicity (elevations in liver transaminases).', NULL, 'Concomitant use of larotrectinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of larotrectinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing the larotrectinib dose by 50%. After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the larotrectinib dose that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', 'Lamivudine, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82834/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Vitrakvi (larotrectinib)." Bayer Pharmaceutical Inc, West Haven, CT.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with