INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82734, 'Delavirdine', 'Lefamulin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and/or P-gp may significantly increase the plasma concentrations of lefamulin, which is a substrate of both the isoenzyme and efflux transporter. Coadministration of intravenous lefamulin with oral ketoconazole increased mean lefamulin Cmax and AUC by 6% and 31%, respectively. Increased exposure to lefamulin may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Concomitant use of lefamulin with potent inhibitors of CYP450 3A4 and/or P-gp should generally be avoided.', NULL, 'Concomitant use of lefamulin with potent inhibitors of CYP450 3A4 and/or P-gp should generally be avoided.', 'Metabolism', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Tinidazole, Linezolid, Bacitracin, Oritavancin, Polymyxin B, Daptomycin, Vancomycin, Metronidazole, Telavancin, Methenamine, Spectinomycin, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82835/', '[1] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King o