INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82767, 'Delavirdine', 'Mifepristone', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of mifepristone, which is primarily metabolized by the isoenzyme. The clinical impact of this interaction has not been studied.', NULL, 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, extreme caution is advised when mifepristone is prescribed in combination with strong CYP450 3A4 inhibitors such as, but not limited to, itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, delavirdine, nefazodone, telithromycin, and protease inhibitors.', NULL, 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, extreme caution is advised when mifepristone is prescribed in combination with strong CYP450 3A4 inhibitors such as, but not limited to, itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, delavirdine, nefazodone, telithromycin, and protease inhibitors. When used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, the dosage of mifepristone should be limited to 300 mg/day and used only when necessary.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, More', 'Raloxifene, Prasterone, Danazol, Bazedoxifene', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82868/', '[1] "Product Information. Korlym (mifepristone)." 