INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82779, 'Delavirdine', 'Nelfinavir', 'Moderate', 'Coadministration with delavirdine may significantly increase the plasma concentrations of nelfinavir. The mechanism is delavirdine inhibition of nelfinavir metabolism via CYP450 3A4 and 2C19.', NULL, 'Appropriate dosages for the combination of nelfinavir and delavirdine with respect to safety and efficacy have not been established.', NULL, 'Appropriate dosages for the combination of nelfinavir and delavirdine with respect to safety and efficacy have not been established. Patients should be monitored for toxicity such as elevations in liver function tests, neutropenia, and undue gastrointestinal disturbances, especially diarrhea.', 'Metabolism', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Acyclovir, More', 'Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, Oseltamivir, Brincidofovir, Remdesivir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82880/', '[1] Developed by the panel of Clinical Practices for Treatment of HIV Infection convened by the Department of Health and Human Services (DHHS) "Guidelines for the use of antiretroviral agents in HIV-1-infected adults and adolescents. Available from: URL: http://AIDSinfo.nih.gov." ([2003 Nov 10]):[2] "Product Information. Rescriptor (delavirdine)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767369485);
