INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82781, 'Delavirdine', 'Nevirapine', 'Moderate', 'The coadministration of delavirdine in combination with other non-nucleoside reverse transcriptase inhibitors (NNRTIs) like efavirenz or nevirapine has not been studied. However, since all three drugs are metabolized by the CYP450 3A4 isoenzyme, pharmacokinetic interactions may theoretically occur. Delavirdine is a potent inhibitor of CYP450 3A4 and may increase the plasma concentrations of both efavirenz and nevirapine. Nevirapine, on the other hand, is an inducer of the isoenzyme and may decrease the plasma concentrations of delavirdine. Efavirenz may have variable effects on delavirdine because it can induce as well as competitively inhibit CYP450 3A4 metabolism.', NULL, 'Clinical monitoring for altered effects of both delavirdine and the other NNRTI may be appropriate following addition or withdrawal of one or the other drug.', NULL, 'Clinical monitoring for altered effects of both delavirdine and the other NNRTI may be appropriate following addition or withdrawal of one or the other drug.', 'Metabolism', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Acyclovir, More', 'Tenofovir alafenamide, Cabotegravir, Zanamivir, Oseltamivir, Brincidofovir, Famciclovir, Voxilaprevir, Tecovirimat, Pibrentasvir, Rimantadine, Telbivudine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82882/', '[1] "Product Information. Rescriptor (delavirdine)." Pharmacia and Upjohn, Kalamazoo, MI.[2] Developed by the panel of Clinical Practices for Treatment of HIV Infection convened by the 