INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82782, 'Delavirdine', 'Nicardipine', 'Moderate', 'Coadministration with delavirdine may increase the plasma concentrations and pharmacologic effects of calcium channel blockers, especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nisoldipine). The mechanism involves delavirdine inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most calcium channel blockers.', NULL, 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with delavirdine.', NULL, 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with delavirdine. Dosage reduction may be required for the calcium channel blocker, particularly if it is a dihydropyridine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Clevidipine', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82883/', '[1] "Product Information. Rescriptor (delavirdine)." Pharmacia and Upjohn, Kalamazoo, MI.[2] Bailey DG, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit juice felodipine interaction: Effect of naringin and 6'',7''-dihydroxybergamottin in humans." Clin Pharmacol Ther 64 (1998): 248-56[3] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[4] Bailey DG, Arnold JMO, Spence JD "Grapefruit juice and drugs - how significant is the interaction." Clin Pharmacokinet 26 (1994): 91-8[5] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[6] Bailey DG, Arnold JM, Munoz C, Spence JD "Grapefruit juice--felodipine interaction: mechanism, predictability, and effect of naringin." Clin Pharmacol Ther 53 (1993): 637-42[7] "Product Information. Procardia (nifedipine)." Pfizer US Pharmaceuticals, New York, NY.[8] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84[9] Edgar B, Bailey D, Bergstrand R, Johnsson G, Regardh CG "Acute effects of drinking grapefruit juice on the pharmacokinetics and dynamics of felodipine--and its potential clinical relevance." Eur J Clin Pharmacol 42 (1992): 313-7[10] Josefsson M, Zackrisson AL, Ahlner J "Effect of grapefruit juice on the pharmacokinetics of amlodipine in healthy volunteers." Eur J Clin Pharmacol 51 (1996): 189-93[11] "Product Information. Plendil (felodipine)." Merck & Co, Inc, West Point, PA.[12] Fuhr U, Muller-Peltzer H, Kern R, et al. "Effects of grapefruit juice and smoking on verapamil concentrations in steady state." Eur J Clin Pharmacol 58 (2002): 45-53[13] Gunston GD, Mehta U "Potentially serious drug interactions with grapefruit juice." S Afr Med J 90 (2000): 41[14] Fuhr U, Maier-Bruggemann A, Blume H, et al. "Grapefruit juice increases oral nimodipine bioavailability." Int J Clin Pharmacol Ther 36 (1998): 126-32[15] "Product Information. Sular (nisoldipine)." Zeneca Pharmaceuticals, Wilmington, DE.[16] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[17] "Grapefruit juice interact