INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82846, 'Delavirdine', 'Rucaparib', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations and the risk of adverse effects of rucaparib, which has been shown in vitro to be a substrate of the isoenzyme. However, the effects on the pharmacokinetics of rucaparib have not been evaluated in vivo.', NULL, 'Caution is advised if rucaparib is used concomitantly with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised if rucaparib is used concomitantly with potent CYP450 3A4 inhibitors. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy with rucaparib.', 'Metabolism', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Porfimer sodium, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82947/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82847, 'Delavirdine', 'Rufinamide', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of delavirdine, which is primarily metabolized by the isoenzyme.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised when delavirdine is used with CYP450 3A4 inducers.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised when delavirdine is used with CYP450 3A4 inducers. Pharmacologic response to the antiretroviral regimen should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral dosage(s) adjusted as necessary.', 'Metabolism', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Velpatasvir, Etravirine, More', 'Topiramate, Ezogabine, Lamotrigine, Ethosuximide, Levetiracetam, Ethotoin, Clonazepam, Paramethadione, Perampanel, Vigabatrin, Mephenytoin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82948/', '[1] Borin MT, Chambers JH, Carel BJ, Gagnon S, Freimuth WW "Pharmacokinetic study of the interaction between rifampin and delavirdine mesylate." Clin Pharmacol Ther 61 (1997): 544-53[2] "Product Information. Rescriptor (delavirdine)." Pharmacia and Upjohn, Kalamazoo, MI.[3] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[4] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in rela