INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82858, 'Delavirdine', 'Simvastatin', 'Major', 'Delavirdine may significantly elevate the plasma concentrations of HMG-CoA reductase inhibitors. The mechanism is decreased clearance due to inhibition of CYP450 3A4 metabolism by delavirdine. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase has been reported occasionally with the use of HMG-CoA reductase inhibitors. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria.', NULL, 'Because of the increased risk of musculoskeletal toxicity associated with high levels of HMG-CoA reductase inhibitory activity in plasma, concurrent use is not recommended by the manufacturer.', NULL, 'Because of the increased risk of musculoskeletal toxicity associated with high levels of HMG-CoA reductase inhibitory activity in plasma, concurrent use is not recommended by the manufacturer. If patients do receive this combination, they should be closely monitored for early signs of myopathy and advised to report symptoms of muscular pain, weakness, tenderness, malaise, or fever. Pravastatin may be a safer alternative, since it is metabolized by a different pathway.', 'Metabolism', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, More', 'Paritaprevir, Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Elbasvir, Doravirine, Bictegravir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82959/', '[1] "Product Information. Rescriptor (delavirdine)." Pharmacia and Upjohn, Kalamazoo, MI.[2] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[3] Lilja JJ, Kivisto KT, Neuvonen PJ "Grapefruit juice-simvastatin interaction: Effect on serum concentrations of simvastatin, simvastatin acid, and HMG-CoA reductase inhibitors." Clin Pharmacol Ther 64 (1998): 477-83[4] Kantola T, Kivisto KT, Neuvonen PJ "Grapefruit juice greatly increases serum concentrations of lovastatin and lovastatin acid." Clin Pharmacol Ther 63 (1998): 397-402[5] "Product Information. Mevacor (lovastatin)." Merck & Co, Inc, West Point, PA.[6] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[7] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008): 463-74[8] Thompson PD, Clarkson P, Karas RH "Statin-associated myopathy." JAMA 289 (2003): 1681-90[9] "Product Information. Zocor (simvastatin)." Merck & Co, Inc, West Point, PA.', 1767369485);
