INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82861, 'Delavirdine', 'Sodium bicarbonate', 'Moderate', 'INTERVAL: Aluminum and magnesium containing antacids have been reported to decrease the bioavailability of delavirdine by an average of 41%. The mechanism may be related to decreased absorption of delavirdine. Other antacids may also interact.', NULL, 'Delavirdine administration should be separated by at least 1 hour from one of these preparations.', NULL, 'Delavirdine administration should be separated by at least 1 hour from one of these preparations.', 'Absorption', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Zanamivir, Lenacapavir, Telaprevir, Etravirine, Cobicistat, Brincidofovir, More', 'Sorbitol, Sulfamethizole, Sodium citrate, Magnesium citrate, Mannitol, Sodium chloride, Chlorhexidine, Neomycin, Calcium chloride, Potassium chloride, Magnesium sulfate, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82962/', '[1] "Product Information. Rescriptor (delavirdine)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82862, 'Delavirdine', 'Solifenacin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of solifenacin, which is primarily metabolized by the isoenzyme.', NULL, 'The dosage of solifenacin should not exceed 5 mg/day when used with potent CYP450 3A4 inhibitors.', NULL, 'The dosage of solifenacin should not exceed 5 mg/day when used with potent CYP450 3A4 inhibitors. Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to contact their physician if they experience undue adverse effects of solifenacin such as severe abdominal pain or constipation for 3 or more days. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope. Concomitant use of solifenacin with potent CYP450 3A4 inhibitors, such as itraconazole, is considered contraindicated by some authorities in patients with severe renal impairment or moderate to severe hepatic impairment during and for 2 weeks after treatment with these drugs.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, More', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82963/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] "Product Information. Sporonox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] "Product Information. VESIcare (solifenacin)." GlaxoSmithKline, Research Triangle Park, NC.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[7] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
