INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82874, 'Delavirdine', 'Tadalafil', 'Moderate', 'Coadministration with drugs that are potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of tadalafil with potent 3A4 inhibitors is not recommended in patients being treated for pulmonary arterial hypertension.', NULL, 'Concomitant use of tadalafil with potent 3A4 inhibitors is not recommended in patients being treated for pulmonary arterial hypertension. In addition, some authorities recommend avoiding concomitant use of tadalafil during and for 2 weeks after treatment with itraconazole in patients with pulmonary arterial hypertension. For erectile dysfunction, tadalafil labeling recommends that the dosage not exceed 10 mg once every 72 hours when taken on an as needed basis in patients treated concomitantly with a potent CYP450 3A4 inhibitor. When taken on a daily basis, the dose should not exceed 2.5 mg per day. Patients should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, Brincidofovir, More', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/in