INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82876, 'Delavirdine', 'Tamsulosin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of tamsulosin, which is primarily metabolized by the hepatic microsomal isoenzymes CYP450 3A4 and 2D6. Severe hypotension and priapism may occur.', NULL, 'Since CYP450 2D6 poor metabolizers cannot be readily identified, concomitant use of tamsulosin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Since CYP450 2D6 poor metabolizers cannot be readily identified, concomitant use of tamsulosin with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of tamsulosin during and for 2 weeks after treatment with itraconazole. If tamsulosin administration is discontinued for several days or more at either the 0.4 or 0.8 mg dose, therapy should be reinitiated with the 0.4 mg once-daily dose and titrated gradually as needed.', 'Metabolism', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Etravirine, Dolutegravir, Brincidofovir, More', 'Finasteride, Terazosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82977/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] "Product Information. Flomax (tamsulosin)." Boehringer-Ingelheim, Ridgefield, CT.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Franco-Salinas G, de la Rosette JJ, Michel