INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82884, 'Delavirdine', 'Terfenadine', 'Major', 'Coadministration with delavirdine may significantly increase the plasma concentrations of astemizole and terfenadine. The mechanism is delavirdine inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of these antihistamines. High plasma levels of astemizole and terfenadine (e.g., due to overdose or interaction with other potent 3A4 inhibitors) have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes; cardiac arrest; and sudden death.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, the concomitant use with delavirdine is considered contraindicated.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, the concomitant use with delavirdine is considered contraindicated. Loratadine, cetirizine, or fexofenadine may be safer alternatives.', 'Metabolism', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Promethazine, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Telaprevir, Velpatasvir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82985/', '[1] "Product Information. Rescriptor (delavirdine)." Pharmacia and Upjohn, Kalamazoo, MI.[2] Honig