INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82897, 'Delavirdine', 'Tretinoin', 'Moderate', 'Coadministration with potent inhibitors of CYP450 2C8, 2C9, and/or 3A4 may increase the plasma concentrations and toxicities of tretinoin, which is a substrate of these isoenzymes. There have been isolated reports of pseudotumour cerebri, hypercalcemia, and acute renal failure in patients receiving tretinoin with fluconazole, itraconazole or voriconazole, all of which are considered potent inhibitors of at least one CYP450 isoenzyme involved in the metabolism of tretinoin. The conditions resolved following interruption of tretinoin therapy and/or discontinuation of the azole antifungal agent.', NULL, 'Caution is advised when tretinoin is prescribed in combination with potent inhibitors of CYP450 2C8, 2C9, and/or 3A4.', NULL, 'Caution is advised when tretinoin is prescribed in combination with potent inhibitors of CYP450 2C8, 2C9, and/or 3A4. Patients should be closely monitored and advised to seek medical attention immediately if they develop early symptoms of pseudotumour cerebri such as headache, nausea, vomiting, visual disturbances, photosensitivity, and tinnitus.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, More', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Porfimer sodium, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82998/', '[1] Cordoba R, Ramirez E, Lei SH, et al. "Hypercalcemia due to an interaction of all-trans reti