INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (82925, 'Delavirdine', 'Zonisamide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of zonisamide, which is metabolized by the isoenzyme. While clinical data are lacking, the possibility of prolonged and/or increased pharmacologic effects of zonisamide should be considered.', NULL, 'Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', NULL, 'Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience symptoms such as severe drowsiness, confusion, loss of coordination, depression, aphasia, decreased sweating, fever, loss of seizure control, or slow pulse.', 'Metabolism', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, More', 'Topiramate, Ezogabine, Lamotrigine, Ethosuximide, Levetiracetam, Ethotoin, Clonazepam, Paramethadione, Perampanel, Vigabatrin, Mephenytoin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/83026/', '[1] Nakasa H, Nakamura H, Ono S, Tsutsui M, Kiuchi M, Ohmori S, Kitada M "Prediction of drug-drug interactions of zonisamide metabolism in humans from in vitro data." Eur J Clin Pharmacol 54 (1998): 177-83[2] "Product Information. Zonegran (zonisamide)" Elan Pharmaceuticals, S. San Francisco, CA.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions b