INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (41548, 'Benzatropine', 'Levodopa', 'Moderate', 'Anticholinergic agents may decrease the absorption and oral bioavailability of levodopa. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents, thereby increasing the gastric degradation of levodopa and reducing the amount available for absorption in the small intestine.', NULL, 'Although certain anticholinergic agents may be used as adjunctive therapy in Parkinson''s disease, clinicians should recognize their potential to reduce the oral bioavailability of levodopa in some patients.', NULL, 'Although certain anticholinergic agents may be used as adjunctive therapy in Parkinson''s disease, clinicians should recognize their potential to reduce the oral bioavailability of levodopa in some patients. Pharmacologic response to levodopa should be monitored more closely whenever anticholinergic agents are added to or withdrawn from therapy, and the dosages of the drugs adjusted as necessary.', 'Absorption', 'Cabergoline', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41604/', '[1] Rivera-Calimlim L, Dujovne CA, Morgan JP, Lasagna L, Bianchine JR "Absorption and metabolism of L-dopa by the human stomach." Eur J Clin Invest 1 (1971): 313-20[2] Bergmann S, Curzon G, Friedel J, et al "The absorption and metabolism of a standard oral dose of levodopa in patients with parkinsonism." Br J Clin Pharmacol 1 (1974): 417-24[3] Algeri S, Cerletti C, Curcio M, et al. "Effect of anticholinergic drugs on gastro-intestinal absorption of L-dopa in rats and man." Eur J Pharmacol 35 (1976