INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (41592, 'Benzatropine', 'Oliceridine', 'Moderate', 'Central anticholinergic agents may have additive central nervous system (CNS) effects with cannabinoids, barbiturates, opiates, and alcohol. These agents individually can cause cognitive and psychomotor impairment, drowsiness, and dizziness, thus concomitant use may result in more potent effects. In addition, the potential for abuse may be increased when central anticholinergic agents are combined with these drugs.', NULL, 'Patients taking central anticholinergic agents in combination with other CNS depressants should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated.', NULL, 'Patients taking central anticholinergic agents in combination with other CNS depressants should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness.', 'Synergism', 'Naltrexone, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41648/', '[1] "Product Information. Artane (trihexyphenidyl)." Lederle Laboratories, Wayne, NJ.', 1767369485);
