INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (41830, 'Gatifloxacin', 'Betamethasone', 'Major', 'Concomitant administration of corticosteroids may potentiate the risk of tendinitis and tendon rupture associated with fluoroquinolone treatment. The mechanism is unknown. Tendinitis and tendon rupture have most frequently involved the Achilles tendon, although cases involving the rotator cuff (the shoulder), the hand, the biceps, and the thumb have also been reported. Some have required surgical repair or resulted in prolonged disability. Tendon rupture can occur during or up to several months after completion of fluoroquinolone therapy.', NULL, 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.', NULL, 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.g., age over 60 years; recipient of kidney, heart, and/or lung transplant). Patients should be advised to stop taking the fluoroquinolone, avoid exercise and use of the affected area, and promptly contact their physician if they experience pain, swelling, or inflammation of a tendon.', 'Synergism', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Vitamin A, Ephedrine, Olopatadine, Ipratropium, Cromoglicic acid, Hyaluronic acid, Nedocromil, More', 'Rifamycin, Kanamycin, Ampicillin, Azithromycin, Acyclovir, Netilmicin, Polymyxin B, Famciclovir, Cefuroxime, Trifluridine, Tobramycin, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/41886/', '[1] Khaliq Y, Zhanel GG "Fluoroquinolone-Associate