INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42019, 'Betamethasone', 'Selpercatinib', 'Moderate', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 3A4 by selpercatinib. The interaction may be particularly important for sensitive substrates or those that demonstrate a narrow therapeutic index.', NULL, 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selpercatinib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Ipratropium, Cromoglicic acid, More', 'Entrectinib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Avapritinib, Bosutinib, Vemurafenib, Selumetinib, Vandetanib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42075/', '[1] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazo