INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42035, 'Betamethasone', 'Sparfloxacin', 'Major', 'Concomitant administration of corticosteroids may potentiate the risk of tendinitis and tendon rupture associated with fluoroquinolone treatment. The mechanism is unknown. Tendinitis and tendon rupture have most frequently involved the Achilles tendon, although cases involving the rotator cuff (the shoulder), the hand, the biceps, and the thumb have also been reported. Some have required surgical repair or resulted in prolonged disability. Tendon rupture can occur during or up to several months after completion of fluoroquinolone therapy.', NULL, 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.', NULL, 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.g., age over 60 years; recipient of kidney, heart, and/or lung transplant). Patients should be advised to stop taking the fluoroquinolone, avoid exercise and use of the affected area, and promptly contact their physician if they experience pain, swelling, or inflammation of a tendon.', 'Synergism', 'Ciclesonide, Mupirocin, Mometasone furoate, Budesonide, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42091/', '[1] Khaliq Y, Zhanel GG "Fluoroquinolone-Associated Tendinopathy: A Critical Review of the Literature." Clin Infect Dis 36 (2003): 1404-1410[2] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[3] van der Linden PD, Sturkenboom MC, Herings RM, Leufkens HM, Rowlands S, Stricker BH "Increased risk of achilles tendon rupture with quinolone antibacterial use, especially in elderly patients taking oral corticosteroids." Arch Intern Med 163 (2003): 1801-7[4] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[5] "Product Information. Baxdela (delafloxacin)." Melinta Therapeutics, Lincolnshire, IL.[6] FDA. U.S. Food and Drug Administration "Information for Healthcare Professionals. Fluoroquinolone Antimicrobial Drugs. FDA Alert [7/8/2008]. Available from: URL: http://www.fda.gov/cder/drug/InfoSheets/HCP/fluoroquinolonesHCP.htm." ([7/8/2008]):[7] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42036, 'Betamethasone', 'Spironolactone', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity. Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', NULL, 'Patients on prolonged (i.', NULL, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure. The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', 'Finerenone', 'Ciclesonide, Mometasone furoate, Mometasone, Vitamin A, Ephedrine, Olopatadine, Ipratropium, Cromoglicic acid, Hyaluronic acid, Nedocromil, Phenylephrine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42092/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (42037, 'Betamethasone', 'Stanozolol', 'Moderate', 'Concomitant use of androgens with adrenocorticotropic hormone (ACTH) or corticosteroids may result in increased fluid retention and edema due to additive steroidal effects.', NULL, 'Caution is advised if androgens are used in combination with ACTH or corticosteroids, particularly in patients with conditions that may be aggravated by fluid retention such as cardiovascular, renal, or hepatic diseases.', NULL, 'Caution is advised if androgens are used in combination with ACTH or corticosteroids, particularly in patients with conditions that may be aggravated by fluid retention such as cardiovascular, renal, or hepatic diseases.', 'Synergism', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', 'Prasterone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/42093/', '[1] "Product Information. Oxandrin (oxandrolone)." Bio-Tech, Levittown, PA.[2] "Product Information. AndroGel (testosterone)." Unimed Pharmaceuticals, Buffalo Grove, IL.', 1767369485);
